#coding:utf-8from selenium import webdriverimport timebrowser = webdriver.Chrome()browser.maximize_window()browser.implicitly_wait(20)browser.get('http://xl.xsjedu.org/js3/?City_id=59&MarketSource=40_1_PC_32221')time.sleep(6)#下面用js来处理现在常见的自定义的弹窗#处理返种弹窗可以刟用 HTML DOM Style 对象，有一个 display 属性 display 的值设置成 none 就可以去除返个弹窗了js = 'document.getElementById("floatWindow").style.display="none";'  #这个要记住browser.execute_script(js)time.sleep(4)print('这个自定义的弹窗已经去掉了，感觉自己棒棒哒')browser.close()browser.quit()