#coding:utf-8from selenium import webdriverimport timefrom selenium.webdriver.common.by import Byfrom selenium.webdriver.support.select import Selectfrom selenium.webdriver.support import expected_conditions as ECfrom selenium.webdriver.common.action_chains import ActionChainsfrom selenium.webdriver.support.wait import WebDriverWaitbrowser = webdriver.Chrome()url = 'https://www.baidu.com'browser.get(url)browser.maximize_window()#locator = (By.LINK_TEXT,'设置')target = WebDriverWait(browser,10,0.5).until(lambda x:x.find_element_by_link_text('设置'))ActionChains(browser).move_to_element(target).perform()#locator2 = (By.LINK_TEXT,'搜索设置')target2 = WebDriverWait(browser,10).until(lambda x:x.find_element_by_link_text('搜索设置'))ActionChains(browser).move_to_element(target2).click().perform()current = browser.current_window_handlebrowser.switch_to.window(current)s = WebDriverWait(browser,10,0.5).until(lambda x:x.find_element_by_id('nr'))Select(s).select_by_visible_text('每页显示50条')#保存设置js = 'document.getElementsByClassName("prefpanelgo")[0].click()'  #这里是个js用法，要仔细揣摩browser.execute_script(js)result = EC.alert_is_present()(browser)print('打印result：%s' % result)if result:    print(result.text)    result.accept()else:    print('alert没弹出')browser.find_element(by=By.ID).sbrowser.close()browser.quit()