#coding:utf-8from selenium import webdriverfrom selenium.webdriver.support import expected_conditions as ECimport timefrom selenium.webdriver.common.by import Bybrowser = webdriver.Chrome()browser.get('https://www.baidu.com')time.sleep(2)browser.maximize_window()time.sleep(4)locator = (By.LINK_TEXT,'地图')text2 = '地图'#browser.fresult = EC.text_to_be_present_in_element(locator,text2)(browser)   #需要传入browser才可调用 见具体用法print (result)#sdjfnsaefnbsoifugbhsaasofihsaoabfosabnfbrowser.close()browser.quit()