#coding:utf-8from selenium import webdriverfrom selenium.webdriver.support.select import Selectfrom selenium.webdriver.common.action_chains import ActionChainsfrom selenium.webdriver.common.alert import Alertimport timebrowser = webdriver.Chrome()browser.get('https://www.baidu.com')browser.maximize_window()time.sleep(4)target = browser.find_element_by_xpath('//*[@id="u1"]/a[8]')ActionChains(browser).move_to_element(target).perform()browser.find_element_by_link_text('搜索设置').click()#browser.find_element_by_xpath('//*[@name="NR"]')current = browser.current_window_handlebrowser.switch_to.window(current)n = browser.find_element_by_xpath('//td[@id="se-setting-3"]/select')Select(n).select_by_value(20)time.sleep(2)browser.find_element_by_link_text('保存设置').click()a = browser.switch_to.alerta.accept()print('定位成功')browser.close()browser.quit()