#coding:utf-8from selenium import webdriverimport time,unittestclass Test(unittest.TestCase):    def test01(self):        a = 1        b = 1        self.assertEqual(a,b)    def test02(self):        x = 'hello'        y = 'hello world!'        self.assertIn(x,y)    def test03(self):        n = True        self.assertTrue(n)    def test04(self):        a = '许广会'        b = '许广会'        self.assertEqual(a,b,msg='失败的原因是：%s != %s' % (a,b))   #还有第三个参数 msg=None,这个 msg 参数就是遇到异常后自定义输出信息if __name__=='__main__':    unittest.main()