#coding:utf-8from selenium import webdriverfrom selenium.webdriver.support import expected_conditions as ECimport time,unittest'''class Test(unittest.TestCase):    def testMinus(self):        #这里是测试减法        result = 6-5        hope = 1        self.assertEqual(result,hope)    def testDivid(self):        #这里测试除法        #这里测试除法        result = 10/5        hope = 2        self.assertEqual(result,hope)'''class Signblog(unittest.TestCase):    def setUp(self):        self.browser = webdriver.Chrome()        self.browser.maximize_window()        self.browser.get('http://www.cnblogs.com/yoyoketang')    def testBlog(self):        time.sleep(3)        result = EC.title_is('上海-悠悠 - 博客园')(self.browser)    #self.browser是必须传入的        print(result)        self.assertTrue(result)    def tearDown(self):        self.browser.close()        self.browser.quit()if __name__=='__main__':    unittest.main()