#coding:utf-8from selenium import webdriverimport timefrom selenium.webdriver.common.action_chains import ActionChainsbrowser = webdriver.Chrome()browser.maximize_window()browser.implicitly_wait(20)browser.get('https://www.baidu.com/')time.sleep(6)#定位到更多产品按钮p = browser.find_element_by_link_text('更多产品')#移动鼠标到更多产品ActionChains(browser).move_to_element(p).perform()time.sleep(3)#点击全部产品all_product = browser.find_element_by_name('tj_more')print(all_product)all_product.click()time.sleep(5)print('已经点击全部产品')browser.close()browser.quit()#看到188页