#coding:utf-8from selenium import webdriverfrom selenium.webdriver.support.select import Selectimport timebrowser = webdriver.Chrome()browser.get('file:///C:/Users/Administrator/Desktop/22.html')browser.maximize_window()time.sleep(4)state = browser.find_element_by_id('boy').is_selected()   #is_selected 是判断该选项是否被选中，若被选中那么是TRUE，否则是Falseprint('状态是：',state)browser.find_element_by_id('boy').click()now_state = browser.find_element_by_id('boy').is_selected()print('现在的状态是：',now_state)time.sleep(8)browser.find_element_by_id('girl').click()time.sleep(4)print('单选测试完毕')boxes = browser.find_elements_by_xpath('//*[contains(@type,"checkbox")]')for i in boxes:    i.click()time.sleep(5)print('复选框测试完毕！')browser.close()browser.quit()