#coding:utf-8from selenium import webdriverimport time,unittestclass Except(unittest.TestCase):    @classmethod    def setUpClass(cls):        cls.browser = webdriver.Chrome()        cls.browser.maximize_window()        url = 'https://www.cnblogs.com/yoyoketang'        cls.browser.get(url)        cls.browser.implicitly_wait(18)    def test_login(self):        self.browser.find_element_by_xpath('//*[@id="mainContent"]/div/div[2]/div[5]/a').click()        time.sleep(2)        self.browser.find_element_by_id('input1').send_keys('不过如此1951')        self.browser.find_element_by_id('input2').send_keys('xgh3213242016.')        time.sleep(2)        self.browser.find_element_by_id('signin').click()        time.sleep(10)        print('下面手动验证。。')    def test_publish(self):        try:            self.browser.find_element_by_link_text('添加新随笔11').click()  #这里会出问题找不到元素        except Exception as e:            print('查找元素出问题：%s' % e)            now_time = time.strftime('%Y_%m_%d_%H.%M.%M')            self.browser.get_screenshot_as_file(r'C:\Users\Administrator\Desktop\%s.png' % now_time)            print('已经把出错页面截图到桌面')    @classmethod    def tearDownClass(cls):        cls.browser.close()        cls.browser.quit()if __name__=='__main__':    unittest.main()