#coding:utf-8from selenium import webdriverimport timeimport randombrowser = webdriver.Chrome()browser.get('http://www.baidu.com')browser.maximize_window()time.sleep(4)browser.find_element_by_id('kw').send_keys(u'测试部落')browser.find_element_by_id('su').click()s = browser.find_elements_by_xpath('//*[@target="_blank"]')print('ok这些是元素：%s' % s)for i in s:    attri = i.get_attribute('href')    print(attri)t = random.randint(0,3)url1 = s[t].get_attribute('href')browser.get(url1)time.sleep(10)browser.close()browser.quit()#看到了第90页