#coding:utf-8from selenium import webdriverfrom selenium.webdriver.common.keys import Keysimport timebrowser = webdriver.Chrome()browser.get('https://www.cnblogs.com/')browser.maximize_window()time.sleep(4)time.sleep(4)blog_url = 'https://www.cnblogs.com/'yoyoblog = blog_url + 'yoyoketang'browser.get(yoyoblog)time.sleep(4)# 1.用js来执行点击事件js1 = 'document.getElementById("blog_nav_admin").click();'browser.execute_script(js1)# 2.用js 里面 class 定位获得是一个 list 列表对象，用哪一个直接用下标，向输入框填值可以直接用value属性赋值#输入账号js_user = 'document.getElementsByClassName("input-text")[0].value="不过如此1951";'browser.execute_script(js_user)#输入密码js_password = 'document.getElementsByClassName("input-text")[1].value="xgh3213242016.";'browser.execute_script(js_password)time.sleep(2)#点击下次自动登录js_remember = 'document.getElementById("remember_me").click();'browser.execute_script(js_remember)time.sleep(1)#点击登录js_sign = 'document.getElementById("signin").click();'browser.execute_script(js_sign)time.sleep(10)print('下面开始手动验证')t = browser.find_element_by_xpath('//div[@id="blog_title"]/a').textprint(t)browser.close()browser.quit()