#coding:utf-8from selenium import webdriverimport timebrowser = webdriver.Chrome()browser.maximize_window()browser.get('file:///C:/Users/Administrator/Desktop/33.html')time.sleep(4)#向下滑动滚动条js_down = 'var x = document.getElementById("yoyoketang").scrollTop=10000'browser.execute_script(js_down)print('向下滑动完成')time.sleep(8)#向上滑动滚动条js_up = 'var y = document.getElementById("yoyoketang").SCROLLtOP=0'browser.execute_script(js_up)print('向上滑动完成')time.sleep(8)#向右滑动js_right = 'var s = document.getElementById("yoyoketang").scrollLeft=10000'browser.execute_script(js_right)print('向右滑动完成')time.sleep(8)#向左滑动js_left = 'var z = document.getElementById("yoyoketang").scrollLeft=0'browser.execute_script(js_left)print('向左滑动完成')time.sleep(8)browser.close()browser.quit()#看到156页