#coding:utf-8from selenium import webdriverimport timebrowser = webdriver.Chrome()browser.maximize_window()browser.implicitly_wait(20)browser.get('http://www.runoob.com/try/try.php?filename=tryjs_alert')time.sleep(4)browser.find_element_by_xpath('/html/body/input').click()  #这里用绝对路径time.sleep(2)aler = browser.switch_to.alerttime.sleep(3)aler.accept()browser.close()browser.quit()