#coding:utf-8from selenium import webdriverfrom selenium.webdriver.support import expected_conditions as ECimport time,unittest#把打开浏览器操作放刡前置 setUpClass(cls)里，返样就可以实现打开一次浏览器，执行多个 case 了class Bloghome(unittest.TestCase):    @classmethod    def setUpClass(cls):        cls.browser = webdriver.Chrome()        cls.browser.maximize_window()        url = 'https://www.cnblogs.com/yoyoketang'        cls.browser.get(url)        cls.browser.implicitly_wait(20)        print('start..')    def test01(self):        #验证元素存在‘博客园’        print('测试用例1')        locator = ("id","blog_nav_sitehome")        text = '博客园'        result = EC.text_to_be_present_in_element(text,locator)      #这里        print(result)        self.assertTrue(result)    def test02(self):        print('测试用例2')        #验证元素存在“新随笔”        locator = ('id',"blog_nav_rss")        text = '新随笔'        result = EC.text_to_be_present_in_element(text,locator)        print(result)        self.assertTrue(result)    @classmethod    def tearDownClass(cls):        cls.browser.close()        cls.browser.quit()        print('end..')if __name__=='__main__':    unittest.main()