#coding:utf-8from selenium import webdriverimport timebrowser = webdriver.Chrome()browser.implicitly_wait(18)browser.maximize_window()print('刚启动浏览的cookie：%s' % browser.get_cookies())'''#browser.get('https://www.baidu.com/')#获取 cookies 方法直接用：get_cookies()blog_url = 'https://www.cnblogs.com/'yoyoblog = blog_url + 'yoyoketang'browser.get(yoyoblog)browser.maximize_window()time.sleep(4)print('浏览网页后的cookies是：%s' % browser.get_cookies())'''#登录页blog_url = 'https://www.cnblogs.com/'yoyoblog = blog_url + 'yoyoketang'browser.get(yoyoblog)time.sleep(4)browser.find_element_by_xpath('//*[@id="mainContent"]/div/div[2]/div[5]/a').click()time.sleep(3)print('登录前的cookie是：%s' % browser.get_cookies())#下面打印登录后cookiebrowser.find_element_by_id('input1').send_keys('不过如此1951')browser.find_element_by_id('input2').send_keys('xgh3213242016')time.sleep(2)browser.find_element_by_xpath('//input[@id="remember_me"]').click()  #记住密码browser.find_element_by_xpath('//input[@type="submit"]').click()time.sleep(18)print('登录成功')print('登录后的cookie是：%s' % browser.get_cookies())#browser.delete_cookie()#browser.delete_all_cookies() 删除所有cookie#browser.add_cookie()browser.close()browser.quit()#看到140页